
public class Fruit {

	String fname;
	int price;
	
	public Fruit(String fname, int price) {
		super();
		this.fname = fname;
		this.price = price;
	}

	public Fruit() {
		// TODO Auto-generated constructor stub
	}

	@Override
	public String toString() {
		return "Fruit [fname=" + fname + ", price=" + price + "]";
	}
	
	
}
